import { User } from '../../../../models';
export default (async (req, res) => {
  const {
    query: {
      id
    }
  } = req;

  const getUser = async obj => {
    return await User.findOne({
      where: obj
    });
  };

  let user = await getUser({
    id: id
  });

  if (user) {
    res.status(200).json({
      user: user
    }); //  next(null, user);
  } else {
    res.status(401).json({
      message: 'No such user found'
    }); //  next(null, false);
  }
});
