import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardContent from "@material-ui/core/CardContent";
import withStyles from "@material-ui/core/styles/withStyles";
import Typography from "@material-ui/core/Typography";
import Person from "@material-ui/icons/Person";
import RemoveRedEye from "@material-ui/icons/RemoveRedEye";
import Room from "@material-ui/icons/Room";
import * as React from "react";
import Img from "react-image";
//import ImageExists from 'image-exists';
import Moment from "react-moment";

// import Button from '../components/Button';

function Capitalize(str) {
  return str.charAt(0).toUpperCase() + str.slice(1);
}

const styles = theme => ({
  gi: {
    paddingTop: "15px",
    paddingBottom: "15px"
  },
  imageWrap: {
    width: "100%",
    paddingBottom: "100%",
    position: "relative",
    borderTop: "1px solid silver",
    borderBottom: "1px solid silver",
    overflow: "hidden",
    width: "30%",
    display: "inline-block",
    paddingBottom: "30%",
    [theme.breakpoints.up("sm")]: {
      width: "100%",
      display: "block",
      paddingBottom: "69%",
      borderTop: "1px solid silver",
      borderBottom: "1px solid silver"
    }
  },
  cat: {
    fontSize: "11px",
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    fontWeight: "400",
    lineHeight: "1.43",
    letterSpacing: "0.01071em",
    padding: "16px",
    paddingBottom: "2px",
    color: "rgba(0, 0, 0, 0.87)",
    paddingTop: "8px",
    overflow: "hidden",
    whiteSpace: "nowrap",
    textOverflow: "ellipsis"
  },
  date: {
    paddingLeft: "16px",
    paddingRight: "16px",
    paddingBottom: "8px",
    color: "rgba(0, 0, 0, 0.54)",
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    fontWeight: "400",
    fontSize: "10px",
    lineHeight: "1.43",
    letterSpacing: "0.01071em",
    [theme.breakpoints.up("sm")]: {
      fontSize: "11px"
    }
  },
  cardLeft: {
    flex: "1 100%",
    background: "#ffffff",
    textAlign: "right",
    [theme.breakpoints.up("sm")]: {
      background: "#e6e6e6",
      textAlign: "left",
      width: "100%"
    }
  },
  cardRight: {
    flex: "0",
    padding: "16px",
    background: "#efefef"
  },
  cHeader: {
    display: "flex",
    position: "absolute",
    left: "30%",
    right: "16px",
    [theme.breakpoints.up("sm")]: {
      position: "relative",
      left: "auto",
      right: "auto"
    }
  },
  price: {
    fontSize: "12px"
  },
  views: {
    color: "black",
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    fontWeight: "400",
    fontSize: "11px",
    lineHeight: "1.43",
    letterSpacing: "0.01071em",
    position: "absolute",
    right: "0",
    bottom: "0",
    zIndex: "99",
    padding: "5px",
    margin: "4px",
    background: "#ffffffcc",
    borderRadius: "4px"
  },
  viewIcon: {
    height: "11px",
    width: "11px",
    color: "rgba(0, 0, 0, 0.54)",
    lineHeight: "1.43"
  },
  title: {
    fontSize: "16px",
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    fontWeight: "500",
    // lineHeight: "1.43",
    letterSpacing: "0.01071em",
    color: "black"
  },
  owner: {
    // paddingLeft: '16px',
    //  paddingBottom: '16px',
    color: "rgba(0, 0, 0, 0.54)",
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    fontWeight: "400",
    fontSize: "11px",
    lineHeight: "1.43",
    letterSpacing: "0.01071em",
    flex: "1",
    overflow: "hidden",
    whiteSpace: "nowrap",
    textOverflow: "ellipsis",
    textAlign: "left"
  },
  personIcon: {
    height: "11px",
    width: "11px",
    color: "rgba(0, 0, 0, 0.54)",
    lineHeight: "1.43",
    color: "rgba(0, 0, 0, 0.54)",
    width: "11px",
    height: "11px",
    lineHeight: "1.43",
    position: "relative",
    top: "2px",
    marginRight: "5px"
  },
  location: {
    // paddingLeft: '16px',
    //  paddingBottom: '16px',
    color: "rgba(0, 0, 0, 0.54)",
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    fontWeight: "400",
    fontSize: "11px",
    lineHeight: "1.43",
    letterSpacing: "0.01071em",
    flex: "1",
    textAlign: "right"
  },
  roomIcon: {
    height: "11px",
    width: "11px",
    color: "rgba(0, 0, 0, 0.54)",
    lineHeight: "1.43",
    color: "rgba(0, 0, 0, 0.54)",
    width: "11px",
    height: "11px",
    lineHeight: "1.43",
    position: "relative",
    top: "2px",
    marginRight: "5px"
  },
  cardBottom: {
    display: "flex"
  },
  card: {
    boxShadow:
      "0 0px 26px 2px rgba(0, 0, 0, 0.14), 0 6px 12px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.2)"
  },
  price: {
    position: "absolute",
    right: "0",
    padding: "6px",
    zIndex: "99",
    color: "white",
    background: "#3b4f63de",
    borderRadius: "4px",
    margin: "4px"
  },
  content: {
    width: "70%",
    float: "right",
    marginTop: "29px",
    [theme.breakpoints.up("sm")]: {
      width: "100%",
      float: "none",
      marginTop: "0"
    }
  }
});

class PostPreview extends React.Component {
  _handleViewPost = id => {
    window.location.href = `/post/${id}`;
  };

  addDefaultSrc = ev => {
    ev.target.src = "/uploadedimages/noimage.jpg";
    console.log(ev.target);
  };

  render() {
    const { classes } = this.props;
    const { post } = this.props;

    var image;

    if (post.files[0]) {
      image = "/uploadedimages/" + post.files[0].name;

      // if (!fs.existsSync(image)) {
      //  image = "/uploadedimages/noimage.jpg";
      // }
    } else image = "/uploadedimages/noimage.jpg";

    //const src = URL + image

    const dateToFormat = this.props.post.updatedAt;

    return (
      //   <GridItem item xs={6} sm={3} className={classes.gi}>
      <Card className={classes.card}>
        <div className={classes.cHeader}>
          <div className={classes.cardLeft}>
            <div className={classes.cat}>
              {this.props.post.catindex} > {this.props.post.keyindex}
            </div>
            <div className={classes.date}>
              <Moment date={dateToFormat} format="D MMMM YY - HH:mm" />
            </div>
          </div>
        </div>
        <CardActionArea onClick={() => this._handleViewPost(post.id)}>
          <div className={classes.imageWrap}>
            {this.props.post.price ? (
              <div className={classes.price}>${this.props.post.price}</div>
            ) : (
              ""
            )}
            <div className={classes.views}>
              <RemoveRedEye className={classes.viewIcon} />{" "}
              {this.props.post.views}
            </div>
            {/* <CardMedia
                component="img"
                alt={this.props.post.title}
                height="100%"
                image={image}
                title={this.props.post.title}
                style={{ position: "absolute", top:'50%', transform: 'translateY(-50%)' }}
                onError={this.addDefaultSrc}
              /> */}
            <Img
              className="img-responsive"
              src={[image, "/uploadedimages/noimage.jpg"]}
              alt={this.props.post.title}
              style={{
                position: "absolute",
                top: "50%",
                transform: "translateY(-50%)",
                width: "100%"
              }}
            />
          </div>
          <CardContent className={classes.content}>
            <Typography
              gutterBottom
              variant="h5"
              component="h3"
              noWrap
              className={classes.title}
            >
              {post.title ? post.title : "untitled"}
            </Typography>
            <div className={classes.cardBottom}>
              <div className={classes.owner}>
                <Person className={classes.personIcon} />{" "}
                {this.props.post.owner}
              </div>
              <div className={classes.location}>
                <Room className={classes.roomIcon} />{" "}
                {this.props.post.cities
                  ? Capitalize(this.props.post.cities)
                  : ""}
              </div>
            </div>
          </CardContent>
        </CardActionArea>
      </Card>
   
    );
  }
}

export default withStyles(styles)(PostPreview);
