function Post(props) {


  const classes = useStyles();




  const router = useRouter()
  const { id } = router.query;

  const { post } = props;
  // let subview = !props.isAuthenticated
  //   ? this.renderLoggedOut()
  //   : this.renderLoggedIn();

  const createdAt = props.post.createdAt;
  const updatedAt = props.post.updatedAt;

  if (props.post.files[0]) {
    var file =
      "url(/uploadedimages/" + props.post.files[0].name + ")";
  } else {
    var file = "url(/uploadedimages/noimage.jpg)";
  }
  const mainImage = {
    backgroundImage: file,
    backgroundSize: "cover",
    backgroundPosition: "center",
    WebkitTransition: "all", // note the capital 'W' here
    msTransition: "all", // 'ms' is the only lowercase vendor prefix}
