    e => e.catindex === props.query.catindex
  );

  //  }
  //if(props.query.keyindex){
  const subcat = props.categories.find(
    e => e.keyindex === props.query.keyindex
  );




  return (
    <Layout user={props.user} categories={props.categories}>
     <PostBreadcrumbs
      post={props.post}
      categories={props.categories}
    />
   <div className={classes.sections}>

      <div className={classes.mainContainer}>
          <div className={classes.mainPost}>
            
      <Grid container spacing={2} className={classes.cardGrid}>
          <Grid item xs={12} md={8}>
          <div className={classes.gallery}>
              <ImageGallery files={props.post.files} />
              </div>
    
            <div className={classes.mainWrap}>
              {/* <div style={mainImage} /> */}
            
              
              <div className={classes.innerWrap}>
                <Grid container>
                  <Grid item xs={12} md={10}>
                    <div className={classes.title}>
                      <h2>{post.title}</h2>
                    </div>
                  </Grid>
                  <Grid item xs={12} md={2}>
                    <div className={classes.price}>
                      <h4 className={classes.price}>
                      {post.price ?  `${post.price} ${post.currency}` : '' }
                      </h4>
                    </div>
                  </Grid>
                  <Grid item xs={12} md={10}>
                    <div className={classes.location}>
                      {post.location} - {post.cities}, {post.country}
                    </div>
                  </Grid>
                  <Grid item xs={12} md={2}>
                    <div className={classes.views}>
                      <RemoveRedEye className={classes.viewicon} />{" "}
                      {props.post.views}
                    </div>
                  </Grid>
                  <Grid item xs={12} md={8}>
                    <p className={classes.description}>
                      {nl2br(post.description)}
                    </p>
                  </Grid>

                  <Grid item xs={12} md={4}>
                    <div className={classes.box}>
                      <div className={classes.datelisted}>
                        Date Listed: <Moment date={createdAt} />
                      </div>
                      <div className={classes.dateupdated}>
                        Date updated: <Moment date={updatedAt} />
                      </div>
                    </div>
                  </Grid>
                </Grid>
              </div>
            </div>
          </Grid>
          <Grid item xs={12} md={4}>
<div className={classes.sidebar}>
<Grid container>
<Grid item xs={12} md={12}>
                    <div className={classes.owner}>
                      <h4>{post.owner}</h4>
                    </div>
                  </Grid>
                  <Grid item xs={12} md={12}>
                    <div className={classes.email}>
                      <h4><a href={`mailto:${post.email}`}>{post.email}</a></h4>
                    </div>
                  </Grid>
                  <Grid item xs={12} md={12}>
                    <div className={classes.phone}>
                      <h4>{post.phone}</h4>
                    </div>
                  </Grid>
                  <Grid item xs={12} md={12}>
                    <div className={classes.website}>
                      <h4><a href={post.website}>{post.website}</a></h4>
                    </div>
                  </Grid>
                  </Grid>

</div>
            </Grid>
          </Grid>
</div>
      </div>
    </div>
  </Layout>
  );
}

Post.getInitialProps = async ({ query, req }) => {

  const id = query.id

  let post = await Queries.getPost(id);
  return { query, post };


};


export default withAuth(Post);



