// Import React FilePond
import React, { useState, useEffect } from "react";
import { FilePond, registerPlugin } from "react-filepond";

// Import FilePond styles
import "filepond/dist/filepond.min.css";

import FilePondPluginFileRename from "filepond-plugin-file-rename";
import FilePondPluginFileValidateSize from "filepond-plugin-file-validate-size";
import FilePondPluginFileValidateType from "filepond-plugin-file-validate-type";
import FilePondPluginImageCrop from "filepond-plugin-image-crop";
// Import the Image EXIF Orientation and Image Preview plugins
// Note: These need to be installed separately
import FilePondPluginImageExifOrientation from "filepond-plugin-image-exif-orientation";
import FilePondPluginImagePreview from "filepond-plugin-image-preview";
import "filepond-plugin-image-preview/dist/filepond-plugin-image-preview.css";
import FilePondPluginImageResize from "filepond-plugin-image-resize";
import FilePondPluginImageTransform from "filepond-plugin-image-transform";
import FilePondPluginImageValidateSize from "filepond-plugin-image-validate-size";

import * as Queries from '../../utils/queries'


import { URL } from "../../config";

// Register the plugins
registerPlugin(
  FilePondPluginImageExifOrientation,
  FilePondPluginImagePreview,
  FilePondPluginImageResize,
  FilePondPluginFileRename,
  FilePondPluginFileValidateSize,
  FilePondPluginFileValidateType,
  FilePondPluginImageCrop,
  FilePondPluginImageValidateSize,
  FilePondPluginImageTransform
);

// Our app
class FileUploader extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      // Set initial files, type 'local' means this is a file
      // that has already been uploaded to the server (see docs)
      //  files: [
      //     {
      //       source: `${URL}/public/uploadedfiles/${this.props.files.name}`,
      //       options: {
      //         type: "local"
      //       }
      //     }
      //  ]
      files: []
    };
  }

  componentDidMount = () => {
    if (this.props.files) {
      var files = [];
      this.props.files.map(file => {
        files.push({
          source: `${URL}/uploadedimages/${file.name}`,
          options: {
            type: "local"
          }
        });
      });

      this.setState({ files: files });
    }
  };

  handleInit() {
    console.log("FilePond instance has initialised", this.pond);
  }

  makeid = length => {
    var result = "";
    var characters =
      "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    var charactersLength = characters.length;
    for (var i = 0; i < length; i++) {
      result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
  };

  render() {
    var n;

    return (
      <div className="App">
        {/* Pass FilePond properties as attributes */}
        {/* <FilePond ref={ref => this.pond = ref}
                          files={this.state.files}
                          allowMultiple={true}
                          maxFiles={10} 
                          name={"file"}
                          fileRenameFunction={file => {
                            n = this.makeid(25);
                            return n + file.extension;
                          }}
                          server="/api/uploadpostfile"
                          oninit={() => this.handleInit() }
                        onupdatefiles={this.props.onupdatefiles }>
                </FilePond> */}
        <FilePond
          ref={this.props.setRef}
          files={this.state.files}
          allowMultiple={true}
          name={"file"}
          maxFiles={10}
        //  server="/api/dashboard/file/postupload"
         // server="/api/uploadpostfile"

         
          // server= {{
          //     process: '/api/dashboard/file/postupload',
          //     fetch: null,
          //     revert: null
          // }}
      

          server={{
            process: "/api/dashboard/file/postupload",
            load: (source, load) => {
                // simulates loading a file from the server
                fetch(source).then(res => res.blob()).then(load);
            },
            remove: (source, load, error) => {

              var name = source.replace(`${URL}/uploadedimages/`, '')            
            
              // Should somehow send `source` to server so server can remove the file with this source
              Queries.deleteFile(name)
              // Can call the error method if something is wrong, should exit after
              error('oh my goodness');
  
              // Should call the load method when done, no parameters required
              load();
          }
          }}
          imageResizeMode="cover"
          allowImageTransform={true}
          allowImageCrop={true}
          imageCropAspectRatio={"1:1"}
          oninit={() => this.handleInit()}
          imageTransformOutputQuality={65}
          imageTransformOutputMimeType="image/jpeg"
          fileRenameFunction={file => {
            n = this.makeid(25);
            return n + file.extension;
          }}
          onupdatefiles={fileItems => {
            //this.props.onupdatefiles({ files: fileItems.map(fileItem => fileItem.file) });
             this.setState({ files: fileItems.map(fileItem => fileItem.file) });
          }}
          // onupdatefiles={this.props.onupdatefiles }
          onDrop={this.props.handleUploadImages}
        />
      </div>
    );
  }
}

export default FileUploader;
